
public class Loops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		int i = 0; 
		
		for( i = 1; i<=10; i ++)
		{
			System.out.println(" For loop is now at: "+i); 
		}
		
		// end of for loop 
		
		System.out.println();// creating a blank line
		
		System.out.println( " End of FOR loop "); 
		
		System.out.println(); // creating a blank line 
		
		i = 10; 
		// beggining or while loop; 
		while ( i >=1)
		{
			System.out.println(" While loop is at: "+i); 
			i--; 
		}
		
		System.out.println(); // creating a blank line 
		
		System.out.println( " End of While loop "); 
		
		System.out.println(); // creating a blank line 
		
		i = 1; 
		
		do 
		{
			System.out.println(" Do while loop is at: "+ i); 
			i++; 
			
		} 
		while( i<=10); 
		 
		 
		
		

	}

}
